#!/usr/bin/php

<?php
  $input = fgets(STDIN);
  if ($unserialized = unserialize($input)) { 
    $input = $unserialized;
  }
  include_once $_SERVER['SP_BUNDLE_PATH'] . '/krumo/class.krumo.php';
  if ((is_object($input) || is_array($input)) && function_exists('krumo')) {
    print krumo($input);
  }
  else {
    print '<pre>' . print_r($input, TRUE) . '</pre>';
  }
?>
